    def retranslateUi(self, Cadastrar):
        _translate = QtCore.QCoreApplication.translate
        Cadastrar.setWindowTitle(_translate("Cadastrar", "Cadastrar"))
        self.btnInicio.setText(_translate("Cadastrar", "INICIO"))
        self.btnCadastrar.setText(_translate("Cadastrar", "CADASTRAR"))
        self.healthypets.setText(_translate("Cadastrar", "HEALTHY PETS"))
        self.btnAgendar.setText(_translate("Cadastrar", "AGENDAR"))
        self.btnProntuario.setText(_translate("Cadastrar", "PRONTUÁRIO"))
        self.txtCadastrar.setText(_translate("Cadastrar", "Cadastrar Cliente"))
        self.nome.setText(_translate("Cadastrar", "NOME DO CLIENTE"))
        self.nascimento.setText(_translate("Cadastrar", "NASCIMENTO"))
        self.telefone.setText(_translate("Cadastrar", "TELEFONE"))
        self.email.setText(_translate("Cadastrar", "E-MAIL"))
        self.rua.setText(_translate("Cadastrar", "RUA"))
        self.bairro.setText(_translate("Cadastrar", "BAIRRO"))
        self.cpf.setText(_translate("Cadastrar", "CPF"))
        self.cidade_2.setText(_translate("Cadastrar", "Nº"))
        self.btnSalvar.setText(_translate("Cadastrar", "ATUALIZAR"))

        def insert(self):
            nomeCliente = self.campoNome.toPlainText()
            cpf = self.campoCPF.toPlainText()
            nascimentoCliente = self.campoNascimento.toPlainText()
            telefone = self.campoTelefone.toPlainText()
            email = self.campoTelefone.toPlainText()
            rua = self.campoRua.toPlainText()
            bairro = self.campoBairro.toPlainText()
            cliente = Cliente(1, nomeCliente, cpf, nascimentoCliente,
                              telefone, email, rua, bairro, 1)
            ClienteController.updateClienteController(cliente)
            msg = QMessageBox()
            # msg.setIcon(QMessageBox.information)
            msg.setText("Cliente Adicionado")
            msg.setWindowTitle("Adicionar Cliente")
            # msg.setStandardButtons(QMessageBox.Ok)
            msg.exec()

        self.btnSalvar.clicked.connect(insert)


