    def selecionarVet(self):
        self.janela_listar_vet = QtWidgets.QMainWindow()
        self.listar_vet = Ui_ListarVetProntuario()
        self.listar_vet.setupUi(self.janela_listar_vet)
        self.janela_listar_vet.show()

    def insert(self):
        raca = self.campoRaca.toPlainText()
        data = self.dateEdit.date().toPyDate()
        especie = self.campoEspecie.toPlainText()
        porte = self.campoPorte.toPlainText()
        sexo = self.campoSexo.toPlainText()
        self.animalId = self.listar_animal.SelecionarAnimal()
        self.veterinarioId = self.listar_vet.SelecionarVet()
        prontuario = Prontuario(1, sexo, porte, especie,
                                data, raca, self.animalId, self.veterinarioId)
        ProntuarioController.controllerProntuario(prontuario)

        msg = QMessageBox()
        # msg.setIcon(QMessageBox.information)
        msg.setText("Prontuário Adicionado")
        msg.setWindowTitle("Adicionar Prontuário")
        # msg.setStandardButtons(QMessageBox.Ok)
        msg.exec()

