    def insert(self):
        nomeVeterinario = self.campoNome.toPlainText()
        cpf = self.campoCPF.toPlainText()
        nascimento = self.dateEdit.date().toPyDate()
        telefone = self.campoTelefone.toPlainText()
        email = self.campoTelefone.toPlainText()
        formacao = self.campoFormacao.toPlainText()

        vet = Veterinario(1, nomeVeterinario, cpf,
                          nascimento, telefone, email, formacao)

        VeterinarioController.controllerVeterinario(vet)

        msg = QMessageBox()
        # msg.setIcon(QMessageBox.information)
        msg.setText("Cliente Adicionado")
        msg.setWindowTitle("Adicionar Cliente")
        # msg.setStandardButtons(QMessageBox.Ok)
        msg.exec()

    def ListVet(self):
        self.janela_listar_vet = QtWidgets.QMainWindow()
        self.lista_vet = Ui_ListarVet()
