    def ExcluirVet(self):
        self.linha = self.listagemVet.currentIndex().row()
        self.idVeterinario = self.listagemVet.item(self.linha, 0).text()
        self.listagemVet.removeRow(self.linha)
        VeterinarioController.deleteControllerVeterinario(self.idVeterinario)

        msg = QMessageBox()
        # msg.setIcon(QMessageBox.information)
        msg.setText("Veterinário Excluida")
        msg.setWindowTitle("Excluir Veterinário")
        # msg.setStandardButtons(QMessageBox.Ok)
        msg.exec()

    def AtualizarVeterinario(self):
        self.linha = self.listagemVet.currentIndex().row()
        self.idVeterinario = self.listagemVet.item(self.linha, 0).text()
        self.janela_att_vet = QtWidgets.QMainWindow()
